const item = $input.first();
const stdout = item.json.stdout || "";

// Étape 1: Extraire les lignes qui se terminent par .java ou .md
const lines = stdout
  .split('\n')
  .map(line => line.trim())
  .filter(line => line.endsWith('.java') || line.endsWith('.markdown') || line.endsWith('.md'));

// Étape 2: Récupérer les items du node "Edit Fields1" de façon sécurisée
let editItems = [];

try {
  const nodeItems = $('Edit Fields1').all();
  for (const e of nodeItems) {
    if (e && e.json) {
      editItems.push(e.json);
    }
  }
} catch (err) {
  // En cas d'erreur, editItems restera vide
  editItems = [];
}

// Étape 3: Construire les résultats
const results = [];

for (const line of lines) {
  const fullPath = line;

  // Chemin relatif
  const relativePath = fullPath.replace('/data/shared/resource/', '');

  const parts = relativePath.split('/');
  const fileName = parts.pop(); // le nom du fichier
  const section = parts.join('/'); // dossier parent

  // Chercher dans editItems celui qui correspond au fichier
  const match = editItems.find(e => e.file_name === fileName);

  results.push({
    json: {
      path: fullPath,
      section: section,
      file: fileName,
      content: match ? match.content : null,
      extractedAt: new Date().toISOString()
    }
  });
}

return results;

const items = [];

for (const item of $input.all()) {
  // Essayez d'abord content, sinon data
  let rawCode = item.json.content || item.json.data;

  // Vérifie que rawCode est une chaîne
  if (typeof rawCode === 'string') {
    try {
      rawCode = rawCode
        .replace(/^```(?:java)?/, '')  // Supprime ```java
        .replace(/```$/, '')           // Supprime ```
        .replace(/\r/g, '')            // Supprime \r
        .trim();                       // Trim les espaces
    } catch (e) {
      // S'il y a une erreur inattendue, garde la valeur d'origine
      rawCode = item.json.content || item.json.data;
    }
  } else {
    // Si ce n’est pas une chaîne, ignore ou met chaîne vide
    rawCode = '';
  }

  // Fonction d’échappement
  const escapeJavaForCypher = (code) => {
    return code
      .replace(/\\/g, '\\\\')
      .replace(/"/g, '\\"')
      .replace(/\n/g, '\\n')
      .replace(/\r/g, '');
  };

  const escapedCode = escapeJavaForCypher(rawCode);

  items.push({
    json: {
      ...item.json,
      cleanedCode: escapedCode,
      extractedAt: new Date().toISOString()
    }
  });
}

return items;

