const files = $input.all().map(item => item.json.d);

const results = [];

// Générer les paires sans doublons et sans auto-paires
for (let i = 0; i < files.length; i++) {
  for (let j = i + 1; j < files.length; j++) {
    const fileA = files[i];
    const fileB = files[j];

    results.push({
      json: {
        source: {
          file: fileA.Source,
          path: fileA.path,
          section: fileA.section,
          content: fileA.content
        },
        target: {
          file: fileB.Source,
          path: fileB.path,
          section: fileB.section,
          content: fileB.content
        }
      }
    });
  }
}

return results;

const inputItems = $input.all();

const outputItems = inputItems.flatMap(item => {
  const {
    source,
    target,
    source_section,
    target_section,
    relationshipType,
    justification
  } = item.json;

  return [
    // Relation normale
    {
      json: {
        source,
        target,
        source_section,
        target_section,
        relationshipType,
        justification
      }
    },
    // Relation inversée (duplicate)
    {
      json: {
        source: target,
        target: source,
        source_section: target_section,
        target_section: source_section,
        relationshipType,
        justification // tu peux ajouter + ' (inversée)' si tu veux l'indiquer
      }
    }
  ];
});

return outputItems;

// Fonction de nettoyage pour Neo4j
function cleanTextForCypher(text) {
  if (!text) return "";

  return text
    .replace(/\\/g, '\\\\')   // Échapper \
    .replace(/"/g, '\\"')     // Échapper "
    .replace(/'/g, "\\'")     // Échapper '
    .replace(/\n/g, ' ')      // Remplacer retour ligne par espace
    .replace(/\r/g, ' ')      // Remplacer retour chariot
    .replace(/\t/g, ' ')      // Remplacer tabulation
    .replace(/[\x00-\x1F\x7F]/g, '') // Supprimer caractères de contrôle
    .replace(/\s+/g, ' ')     // Réduire les espaces multiples
    .trim();
}

// Entrée
const raw = $input.first().json.output || "";

// Nettoyage brut des balises Markdown (```json ou ```)
const cleaned = raw.replace(/```json|```/g, '').trim();

let parsed;
try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error('Erreur de parsing du JSON dans le champ "output" : ' + err.message);
}

// Nettoyage de la justification
const cleanedJustification = cleanTextForCypher(parsed.justification);

// Construction du résultat
return [
  {
    json: {
      source: parsed.source,
      target: parsed.target,
      source_section: parsed.source_section,
      target_section: parsed.target_section,
      relationshipType: parsed.type,
      justification: cleanedJustification,
      extractedAt: new Date().toISOString()
    }
  }
];
