const batchItems = $input.all();

console.log(`Génération requête pour batch de ${batchItems.length} items`);

function escapeCypher(value) {
  if (!value || typeof value !== 'string') return '';

  return value
    .replace(/\\/g, '\\\\')
    .replace(/"/g, '\\"')
    .replace(/'/g, "\\'")
    .replace(/\n/g, '\\n')
    .replace(/\r/g, '')
    .replace(/\t/g, '\\t')
    .substring(0, 8000);
}

const mergeStatements = [];

for (let i = 0; i < batchItems.length; i++) {
  const item = batchItems[i];

  try {
    if (!item.json.file_name || !item.json.PATH) {
      console.warn(`Item ${i} ignoré: champs manquants`);
      continue;
    }

    const fileName = escapeCypher(item.json.file_name);
    const path = escapeCypher(item.json.PATH);
    const content = escapeCypher(item.json.content);
    const section = escapeCypher(item.json.section || '');
    const extractedAt = item.json.extractedAT || new Date().toISOString();

    const mergeStatement = `
MERGE (d${i}:TestGraph1 {Source: "${fileName}", path: "${path}"}) 
ON CREATE SET   
  d${i}.content = "${content}",
  d${i}.section = "${section}",
  d${i}.extractedAt = "${extractedAt}",
  d${i}.path = "${path}"
ON MATCH SET
  d${i}.content = "${content}",
  d${i}.section = "${section}",
  d${i}.extractedAt = "${extractedAt}"`;

    mergeStatements.push(mergeStatement);
  } catch (error) {
    console.error(`Erreur item ${i}:`, error.message);
  }
}

const cypherQuery = mergeStatements.join('\n') +
  `\nRETURN ${mergeStatements.map((_, i) => `d${i}.Source as source${i}`).join(', ')}`;

console.log(`Requête générée avec ${mergeStatements.length} MERGE statements`);

// Sortie 1 : la requête Cypher
const queryOutput = {
  json: {
    cypherQuery,
    batchSize: mergeStatements.length,
    processedAt: new Date().toISOString()
  }
};

// Sortie 2..n : les items originaux
const originalItemsOutput = batchItems;

return [queryOutput, ...originalItemsOutput];

