// Fonction de nettoyage pour Neo4j
function cleanTextForCypher(text) {
  if (!text) return "";
  return text
    .replace(/\\/g, '\\\\')   // Échapper \
    .replace(/"/g, '\\"')     // Échapper "
    .replace(/'/g, "\\'")     // Échapper '
    .replace(/\n/g, ' ')      // Remplacer retour ligne par espace
    .replace(/\r/g, ' ')      // Remplacer retour chariot
    .replace(/\t/g, ' ')      // Remplacer tabulation
    .replace(/[\x00-\x1F\x7F]/g, '') // Supprimer caractères de contrôle
    .replace(/\s+/g, ' ')     // Réduire les espaces multiples
    .trim();
}

// Fonction pour traiter un seul item
function processItem(item, index) {
  try {
    // Extraction de la sortie brute
    const raw = item.json.output || "";

    // Nettoyage brut des balises Markdown (```json ou ```)
    const cleaned = raw.replace(/```json|```/g, '').trim();

    // Parsing JSON
    const parsed = JSON.parse(cleaned);

    // Nettoyage de la justification
    const cleanedJustification = cleanTextForCypher(parsed.justification);

    // Construction du résultat pour cet item
    return {
      json: {
        source: parsed.source,
        target: parsed.target,
        source_section: parsed.source_section,
        target_section: parsed.target_section,
        relationshipType: parsed.type,
        justification: cleanedJustification,
        extractedAt: new Date().toISOString(),
        batchIndex: index // Optionnel : pour tracer l'ordre
      }
    };

  } catch (err) {
    // En cas d'erreur, retourner un objet d'erreur
    return {
      json: {
        error: true,
        errorMessage: `Erreur item ${index}: ${err.message}`,
        originalOutput: item.json.output,
        extractedAt: new Date().toISOString(),
        batchIndex: index
      }
    };
  }
}

// Traitement de tous les items
const allItems = $input.all(); // Récupère TOUS les items du batch
const results = [];

// Boucle sur chaque item
for (let i = 0; i < allItems.length; i++) {
