    try {
      rawCode = rawCode
        .replace(/^```(?:java)?/, '')  // Supprime ```java
        .replace(/```$/, '')           // Supprime ```
        .replace(/\r/g, '')            // Supprime \r
        .trim();                       // Trim les espaces
    } catch (e) {
      // S'il y a une erreur inattendue, garde la valeur d'origine
      rawCode = item.json.content || item.json.data;
    }
  } else {
    // Si ce n’est pas une chaîne, ignore ou met chaîne vide
    rawCode = '';
  }

  // Fonction d’échappement
  const escapeJavaForCypher = (code) => {
    return code
      .replace(/\\/g, '\\\\')
      .replace(/"/g, '\\"')
      .replace(/\n/g, '\\n')
      .replace(/\r/g, '');
  };

  const escapedCode = escapeJavaForCypher(rawCode);

  items.push({
    json: {
      ...item.json,
      cleanedCode: escapedCode,
      extractedAt: new Date().toISOString()
    }
  });
}

return items;

const allFiles = $input.all().map(item => item.json);
const totalFiles = allFiles.length;
const maxPairsLimit = 50000; // Limite pour éviter surcharge mémoire

console.log(`Génération optimisée pour ${totalFiles} fichiers (limite: ${maxPairsLimit})`);

const results = [];
let totalPairsGenerated = 0;
const totalPossiblePairs = totalFiles * (totalFiles - 1) / 2;

// Si trop de paires possibles, utiliser échantillonnage
const useRandomSampling = totalPossiblePairs > maxPairsLimit;
const samplingRate = useRandomSampling ? maxPairsLimit / totalPossiblePairs : 1;

console.log(`Mode: ${useRandomSampling ? 'Échantillonnage' : 'Complet'} (taux: ${samplingRate.toFixed(4)})`);

for (let i = 0; i < totalFiles - 1 && totalPairsGenerated < maxPairsLimit; i++) {
  const currentFile = allFiles[i];

  for (let j = i + 1; j < totalFiles && totalPairsGenerated < maxPairsLimit; j++) {
    const targetFile = allFiles[j];

    // Échantillonnage aléatoire si nécessaire
    if (!useRandomSampling || Math.random() < samplingRate) {
      results.push({
        json: {
          source: {
            file: currentFile.file_name || currentFile.Source,
            path: currentFile.PATH || currentFile.path,
            section: currentFile.section,
            content: currentFile.content
          },
          target: {
            file: targetFile.file_name || targetFile.Source,
            path: targetFile.PATH || targetFile.path,
            section: targetFile.section,
            content: targetFile.content
          },
          pairIndex: `${i + 1}-${j + 1}`,
          sampled: useRandomSampling
        }
      });

      totalPairsGenerated++;
    }
  }

  // Log de progression
  if (i % 10 === 0) {
    console.log(`Itération ${i + 1}/${totalFiles - 1}: ${totalPairsGenerated} paires générées`);
  }
}

console.log(`TERMINÉ: ${totalPairsGenerated} paires sur ${totalPossiblePairs} possibles`);
return results;
